mapper.insert:
insert into tag(
tag_num,
board_num,
tag
)
values(
tag_seq.nextval,
#{board_num},
#{tag}
)

mapper.update:
update member_act_cnt 
set board_write_cnt = board_write_cnt+ 1 
where member_num = #{memberNum}

mapper.select:
select 
tag
from tag where board_num = #{boardNum}

mapper.update:
update board
set 
	tripdate = #{tripdate},
	place= #{place},
	content= #{content}
where board_num = #{board_num}
